require 'rails_helper'

RSpec.describe Rest::V1::AuthenticationController, type: :api   do
	 include RSpec::Rails::ControllerExampleGroup
   
  	it "Debe retornar error al enviar request vacio a login" do
  		resp= post 'login'
  		expect(resp.code).not_to eq('200')
  	end

  	it "Debe retornar un estado exitoso al enviar correo valido a login" do
  		resp= post 'login', params: {"email":"ignacio@acid.cl", "image":""}
  		expect(resp.code).to eq("200")
  	end
   
   	it "No debe permitir conectar directamente a servicio verify_user " do 
   		resp =  post 'verify', params: {"id":"ignacio@acid.cl", "image":""}
   		expect(resp.code).to eq("403")
   	end
end
